import React, { useRef, useEffect, useState } from "react";
import imageSrc from "./booker-t-washington.jpg"; // Place 1080p image here

const GRID_COLUMNS = 100; // Width divisions
const GRID_ROWS = 100; // Height divisions
const CANVAS_WIDTH = 1920;
const CANVAS_HEIGHT = 1080;
const CELL_WIDTH = CANVAS_WIDTH / GRID_COLUMNS;
const CELL_HEIGHT = CANVAS_HEIGHT / GRID_ROWS;

function App() {
  const canvasRef = useRef(null);
  const [hoveredCell, setHoveredCell] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [mockClaims, setMockClaims] = useState(generateMockClaims());

  useEffect(() => {
    drawCanvas();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [mockClaims, hoveredCell]);

  const drawCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.src = imageSrc;
    img.onload = () => {
      ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
      ctx.drawImage(img, 0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
      drawGrid(ctx);
      drawClaims(ctx);
      if (hoveredCell) {
        highlightHoveredCell(ctx, hoveredCell);
      }
    };
  };

  const drawGrid = (ctx) => {
    ctx.strokeStyle = "rgba(0,0,0,0.1)";
    for (let i = 0; i <= GRID_COLUMNS; i++) {
      ctx.beginPath();
      ctx.moveTo(i * CELL_WIDTH, 0);
      ctx.lineTo(i * CELL_WIDTH, CANVAS_HEIGHT);
      ctx.stroke();
    }

    for (let j = 0; j <= GRID_ROWS; j++) {
      ctx.beginPath();
      ctx.moveTo(0, j * CELL_HEIGHT);
      ctx.lineTo(CANVAS_WIDTH, j * CELL_HEIGHT);
      ctx.stroke();
    }
  };

  const drawClaims = (ctx) => {
    mockClaims.forEach(({ x, y }) => {
      ctx.fillStyle = "rgba(218,165,32,0.5)"; // gold color for respect
      ctx.fillRect(x * CELL_WIDTH, y * CELL_HEIGHT, CELL_WIDTH, CELL_HEIGHT);
    });
  };

  const highlightHoveredCell = (ctx, cell) => {
    ctx.fillStyle = "rgba(0, 0, 0, 0.15)";
    ctx.fillRect(
      cell.x * CELL_WIDTH,
      cell.y * CELL_HEIGHT,
      CELL_WIDTH,
      CELL_HEIGHT
    );
  };

  const handleMouseMove = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = Math.floor((e.clientX - rect.left) / CELL_WIDTH);
    const y = Math.floor((e.clientY - rect.top) / CELL_HEIGHT);
    setHoveredCell({ x, y });
    setMousePosition({ x: e.clientX - rect.left, y: e.clientY - rect.top });
  };

  const handleClick = () => {
    if (hoveredCell) {
      const isClaimed = mockClaims.some(
        (cell) => cell.x === hoveredCell.x && cell.y === hoveredCell.y
      );
      alert(
        `Cell (${hoveredCell.x}, ${hoveredCell.y}) is ${
          isClaimed ? "CLAIMED" : "AVAILABLE"
        }`
      );
    }
  };

  return (
    <div
      style={{ textAlign: "center", marginTop: "20px", position: "relative" }}
    >
      <h1>Million Pixel Booker T. Washington</h1>
      <div style={{ position: "relative", display: "inline-block" }}>
        <canvas
          ref={canvasRef}
          width={CANVAS_WIDTH}
          height={CANVAS_HEIGHT}
          onMouseMove={handleMouseMove}
          onClick={handleClick}
          style={{
            border: "1px solid black",
            maxWidth: "100%",
            height: "auto",
          }}
        />
        {hoveredCell && (
          <div
            style={{
              position: "absolute",
              top: mousePosition.y + 10,
              left: mousePosition.x + 10,
              backgroundColor: "white",
              border: "1px solid black",
              padding: "5px",
              pointerEvents: "none",
            }}
          >
            Cell: ({hoveredCell.x}, {hoveredCell.y})
          </div>
        )}
      </div>
    </div>
  );
}

function generateMockClaims() {
  const claims = [];
  for (let i = 0; i < 300; i++) {
    claims.push({
      x: Math.floor(Math.random() * GRID_COLUMNS),
      y: Math.floor(Math.random() * GRID_ROWS),
    });
  }
  return claims;
}

export default App;
