import React, { useEffect, useMemo, useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
  useNavigate,
} from "react-router-dom";
import { io } from "socket.io-client";
import CanvasPage from "./pages/CanvasPage";
import PaymentIntro from "./pages/PaymentIntro";
import SimulatePage from "./pages/SimulatePage";

// -------- shared envs --------
const API_BASE =
  process.env.REACT_APP_API_BASE ||
  (import.meta?.env && import.meta.env.VITE_API_BASE) ||
  "http://localhost:3001";

// Keep in sync with your canvas
const GRID_COLUMNS = 200;
const GRID_ROWS = 200;

export default function App() {
  return (
    <Router>
      <StyleNav />
      <NavBar />
      <main>
        <Routes>
          <Route path="/" element={<CanvasPage />} />
          <Route path="/support" element={<PaymentIntro />} />
          <Route path="/simulate" element={<SimulatePage />} />
        </Routes>
      </main>
    </Router>
  );
}

function NavBar() {
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [revealedCount, setRevealedCount] = useState(0);

  const totalCells = GRID_COLUMNS * GRID_ROWS;
  const remaining = useMemo(
    () => Math.max(totalCells - revealedCount, 0),
    [revealedCount, totalCells]
  );

  // bootstrap + live updates
  useEffect(() => {
    let s;
    (async () => {
      try {
        const r = await fetch(`${API_BASE}/state`);
        const { cells } = await r.json();
        setRevealedCount(Array.isArray(cells) ? cells.length : 0);
      } catch {}
      // live
      s = io(API_BASE, { transports: ["websocket"] });
      const onMerge = ({ cells }) => {
        if (!cells?.length) return;
        // Just increment by delta; avoids keeping a big set in nav
        setRevealedCount((n) => {
          // naive add: assumes backend only sends *new* cells in this event
          const delta = cells.length;
          return n + delta;
        });
      };
      s.on("bootstrap", ({ cells }) =>
        setRevealedCount(Array.isArray(cells) ? cells.length : 0)
      );
      s.on("cells_revealed", onMerge);
    })();
    return () => {
      try {
        s && s.disconnect();
      } catch {}
    };
  }, []);

  function gotoSupport() {
    setOpen(false);
    navigate("/support");
  }

  return (
    <header className="nav">
      <div className="nav-inner">
        {/* left: brand */}
        <div
          className="brand"
          onClick={() => navigate("/")}
          role="button"
          tabIndex={0}
        >
          <div className="logo-dot" />
          <span className="brand-text">Million Pixel</span>
        </div>

        {/* center: links (desktop) */}
        <nav className="links">
          <Nav to="/" label="Canvas" />
          <Nav to="/support" label="Support" />
          <Nav to="/simulate" label="Simulate" />
        </nav>

        {/* right: remaining + CTA */}
        <div className="right">
          <div className="pill" title="Cells remaining to reveal">
            <span className="pill-dot" />
            <span className="pill-text">Remaining</span>
            <span className="pill-num">{remaining.toLocaleString()}</span>
          </div>
          <button className="cta" onClick={gotoSupport}>
            Support
          </button>

          {/* mobile toggle */}
          <button
            className="hamburger"
            aria-label="Toggle menu"
            aria-expanded={open}
            onClick={() => setOpen((o) => !o)}
          >
            <span />
            <span />
            <span />
          </button>
        </div>
      </div>

      {/* mobile drawer */}
      <div
        className={`drawer ${open ? "open" : ""}`}
        onClick={() => setOpen(false)}
      >
        <div className="drawer-inner" onClick={(e) => e.stopPropagation()}>
          <Nav to="/" label="Canvas" onClick={() => setOpen(false)} />
          <Nav to="/support" label="Support" onClick={() => setOpen(false)} />
          <Nav to="/simulate" label="Simulate" onClick={() => setOpen(false)} />
          <div className="drawer-sep" />
          <div className="drawer-remaining">
            <span>Remaining:</span> <b>{remaining.toLocaleString()}</b>
          </div>
          <button className="cta wide" onClick={gotoSupport}>
            Support
          </button>
        </div>
      </div>
    </header>
  );
}

function Nav({ to, label, onClick }) {
  return (
    <NavLink
      to={to}
      onClick={onClick}
      className={({ isActive }) => `link ${isActive ? "active" : ""}`}
    >
      {label}
    </NavLink>
  );
}

/* ---------- styles for the nav ---------- */
function StyleNav() {
  return (
    <style>{`
:root{
  --nav-bg: rgba(9,14,24,.8);
  --line: #334155;
  --text: #e2e8f0;
  --muted: #94a3b8;
  --brand: #16a34a;
}

.nav{
  position: sticky;
  top: 0;
  z-index: 50;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  background: linear-gradient(180deg, rgba(255,255,255,.03), rgba(255,255,255,.01)), var(--nav-bg);
  border-bottom: 1px solid rgba(51,65,85,.6);
}

.nav-inner{
  height: 64px;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 14px;
  display: grid;
  grid-template-columns: auto 1fr auto;
  align-items: center;
  gap: 12px;
  color: var(--text);
}

.brand{
  display: flex; align-items: center; gap: 10px;
  cursor: pointer; user-select: none;
}
.logo-dot{
  width: 18px; height: 18px; border-radius: 6px;
  background: linear-gradient(180deg, #24d069, #16a34a);
  box-shadow: 0 4px 12px rgba(22,163,74,.4);
}
.brand-text{
  font-weight: 800; letter-spacing: .2px;
}

.links{
  display: flex; gap: 12px; align-items: center;
}
.link{
  padding: 8px 10px; border-radius: 8px;
  color: var(--text);
  text-decoration: none;
  transition: background .12s ease, color .12s ease;
}
.link:hover{ background: rgba(255,255,255,.04); }
.link.active{
  color: white;
  background: rgba(22,163,74,.12);
  outline: 1px solid rgba(22,163,74,.35);
}

.right{
  display: flex; gap: 10px; align-items: center;
}

.pill{
  display: inline-flex; align-items: center; gap: 8px;
  height: 36px; padding: 0 10px;
  background: rgba(2,6,23,.6);
  border: 1px solid rgba(51,65,85,.6);
  border-radius: 999px;
}
.pill-dot{
  width: 8px; height: 8px; border-radius: 50%;
  background: #22c55e;
  box-shadow: 0 0 10px rgba(34,197,94,.8);
}
.pill-text{ color: var(--muted); font-size: 12px; }
.pill-num{ font-weight: 800; letter-spacing: .3px; }

.cta{
  appearance: none; border: none; cursor: pointer;
  background: var(--brand); color: white; font-weight: 700;
  padding: 10px 14px; border-radius: 10px;
  box-shadow: 0 6px 20px rgba(22,163,74,.25);
}
.cta.wide{ width: 100%; margin-top: 8px; }

.hamburger{
  display: none;
  width: 40px; height: 36px;
  background: rgba(255,255,255,.04);
  border: 1px solid rgba(51,65,85,.6);
  border-radius: 8px; cursor: pointer;
  padding: 8px;
}
.hamburger span{
  display: block; height: 2px; background: var(--text);
  margin: 4px 0; border-radius: 2px;
}

@media (max-width: 760px){
  .links{ display: none; }
  .hamburger{ display: inline-block; }
}

.drawer{
  position: fixed; inset: 64px 0 0 0;  /* below the nav */
  background: rgba(2,6,23,.6);
  opacity: 0; pointer-events: none; transition: opacity .12s ease;
}
.drawer.open{ opacity: 1; pointer-events: auto; }
