import React, { useRef, useEffect, useState } from "react";
import imageSrc from "./booker-t-washington.jpg";

const GRID_COLUMNS = 100;
const GRID_ROWS = 100;
const CANVAS_WIDTH = 1920;
const CANVAS_HEIGHT = 1080;
const CELL_WIDTH = CANVAS_WIDTH / GRID_COLUMNS;
const CELL_HEIGHT = CANVAS_HEIGHT / GRID_ROWS;

function App() {
  const canvasRef = useRef(null);
  const [hoveredCell, setHoveredCell] = useState(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [mockClaims, setMockClaims] = useState(generateMockClaims());
  const [selectedClaim, setSelectedClaim] = useState(null);
  const [showDetails, setShowDetails] = useState(true);

  useEffect(() => {
    drawCanvas();
  }, [mockClaims, hoveredCell]);

  const drawCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.src = imageSrc;
    img.onload = () => {
      ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
      ctx.drawImage(img, 0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
      drawGrid(ctx);
      drawClaims(ctx);
      if (hoveredCell) {
        highlightHoveredCell(ctx, hoveredCell);
      }
    };
  };

  const drawGrid = (ctx) => {
    ctx.strokeStyle = "rgba(0,0,0,0.1)";
    for (let i = 0; i <= GRID_COLUMNS; i++) {
      ctx.beginPath();
      ctx.moveTo(i * CELL_WIDTH, 0);
      ctx.lineTo(i * CELL_WIDTH, CANVAS_HEIGHT);
      ctx.stroke();
    }

    for (let j = 0; j <= GRID_ROWS; j++) {
      ctx.beginPath();
      ctx.moveTo(0, j * CELL_HEIGHT);
      ctx.lineTo(CANVAS_WIDTH, j * CELL_HEIGHT);
      ctx.stroke();
    }
  };

  const drawClaims = (ctx) => {
    mockClaims.forEach(({ x, y }) => {
      ctx.fillStyle = "rgba(218,165,32,0.5)";
      ctx.fillRect(x * CELL_WIDTH, y * CELL_HEIGHT, CELL_WIDTH, CELL_HEIGHT);
    });
  };

  const highlightHoveredCell = (ctx, cell) => {
    if (!cell) return;
    ctx.fillStyle = "rgba(0, 0, 0, 0.15)";
    ctx.fillRect(
      cell.x * CELL_WIDTH,
      cell.y * CELL_HEIGHT,
      CELL_WIDTH,
      CELL_HEIGHT
    );
  };

  const handleMouseMove = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();

    const scaleX = canvas.width / rect.width;
    const scaleY = canvas.height / rect.height;

    const canvasX = (e.clientX - rect.left) * scaleX;
    const canvasY = (e.clientY - rect.top) * scaleY;

    const x = Math.floor(canvasX / CELL_WIDTH);
    const y = Math.floor(canvasY / CELL_HEIGHT);

    setHoveredCell({ x, y });
    setMousePosition({ x: e.clientX - rect.left, y: e.clientY - rect.top });
  };

  const handleClick = () => {
    if (hoveredCell) {
      const claim = getClaimInfo(hoveredCell);
      setSelectedClaim(
        claim
          ? { ...claim, coords: hoveredCell }
          : { coords: hoveredCell, name: null }
      );
    }
  };

  const getClaimInfo = (cell) => {
    return mockClaims.find((claim) => claim.x === cell.x && claim.y === cell.y);
  };

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>
        Million Pixel Booker T. Washington
      </h1>
      <button
        onClick={() => setShowDetails(!showDetails)}
        style={{ margin: "10px" }}
      >
        {showDetails ? "Hide Details" : "Show Details"}
      </button>
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "center",
          marginTop: "20px",
        }}
      >
        <div
          style={{
            position: "relative",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <canvas
            ref={canvasRef}
            width={CANVAS_WIDTH}
            height={CANVAS_HEIGHT}
            onMouseMove={handleMouseMove}
            onClick={handleClick}
            style={{
              border: "1px solid black",
              maxWidth: "100%",
              height: "auto",
            }}
          />
          {hoveredCell && (
            <div
              style={{
                position: "absolute",
                top: mousePosition.y + 10,
                left: mousePosition.x + 10,
                backgroundColor: "white",
                border: "1px solid black",
                padding: "5px",
                pointerEvents: "none",
                maxWidth: "200px",
              }}
            >
              {(() => {
                const claim = getClaimInfo(hoveredCell);
                return claim ? (
                  <>
                    <div>
                      Cell: ({hoveredCell.x}, {hoveredCell.y})
                    </div>
                    <div>
                      <strong>Claimed by:</strong> {claim.name}
                    </div>
                    {claim.message && (
                      <div>
                        <em>"{claim.message}"</em>
                      </div>
                    )}
                  </>
                ) : (
                  <div>
                    Cell: ({hoveredCell.x}, {hoveredCell.y}) - Available
                  </div>
                );
              })()}
            </div>
          )}
        </div>
        {showDetails && (
          <div
            style={{
              width: "300px",
              marginLeft: "20px",
              textAlign: "left",
              borderLeft: "1px solid #ccc",
              paddingLeft: "10px",
            }}
          >
            <h2>Cell Details</h2>
            {selectedClaim ? (
              selectedClaim.name ? (
                <>
                  <p>
                    <strong>Coordinates:</strong> ({selectedClaim.coords.x},{" "}
                    {selectedClaim.coords.y})
                  </p>
                  <p>
                    <strong>Claimed by:</strong> {selectedClaim.name}
                  </p>
                  {selectedClaim.message && (
                    <p>
                      <em>"{selectedClaim.message}"</em>
                    </p>
                  )}
                </>
              ) : (
                <p>
                  Coordinates: ({selectedClaim.coords.x},{" "}
                  {selectedClaim.coords.y}) - Available
                </p>
              )
            ) : (
              <p>Click a cell to see details here.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

function generateMockClaims() {
  const claims = [];
  for (let i = 0; i < 300; i++) {
    claims.push({
      x: Math.floor(Math.random() * GRID_COLUMNS),
      y: Math.floor(Math.random() * GRID_ROWS),
      name: `Donor ${i}`,
      message: Math.random() > 0.7 ? "Supporting the legacy!" : null,
    });
  }
  return claims;
}

export default App;
