import React, { useRef, useEffect, useState } from "react";
import { io } from "socket.io-client";
import imageSrc from "../Banner Hi Rez.png";

const API_BASE =
  process.env.REACT_APP_API_BASE ||
  (import.meta?.env && import.meta.env.VITE_API_BASE) ||
  "http://localhost:3001";

// Canvas + grid
const CANVAS_WIDTH = 1495;
const CANVAS_HEIGHT = 1024;
const GRID_COLUMNS = 200; // 200x200 = 40,000 cells
const GRID_ROWS = 200;
const CELL_WIDTH = CANVAS_WIDTH / GRID_COLUMNS;
const CELL_HEIGHT = CANVAS_HEIGHT / GRID_ROWS;

export default function CanvasPage() {
  const canvasRef = useRef(null);
  const imgRef = useRef(null);
  const [imgReady, setImgReady] = useState(false);

  const [revealedCells, setRevealedCells] = useState([]);
  const [toasts, setToasts] = useState([]); // { id, text }
  const totalCells = GRID_COLUMNS * GRID_ROWS;

  // --- toast helpers ---
  function addToast(text, ms = 8000) {
    if (!text) return;
    const id = Math.random().toString(36).slice(2);
    setToasts((t) => [...t, { id, text }]);
    setTimeout(() => setToasts((t) => t.filter((x) => x.id !== id)), ms);
  }

  // --- preload image once ---
  useEffect(() => {
    const img = new Image();
    img.src = imageSrc;
    img.onload = () => {
      imgRef.current = img;
      setImgReady(true);
    };
    img.onerror = () => console.error("Failed to load canvas image");
  }, []);

  // --- sockets + bootstrap state ---
  useEffect(() => {
    const socket = io(API_BASE, { transports: ["websocket"] });

    const merge = (incoming) =>
      setRevealedCells((prev) => {
        const seen = new Set(prev.map((c) => `${c.x},${c.y}`));
        const out = [...prev];
        for (const c of incoming) if (!seen.has(`${c.x},${c.y}`)) out.push(c);
        return out;
      });

    fetch(`${API_BASE}/state`)
      .then((r) => r.json())
      .then(({ cells }) => merge(cells))
      .catch(() => {});

    socket.on("bootstrap", ({ cells }) => merge(cells));
    socket.on("cells_revealed", ({ cells, message }) => {
      merge(cells);
      if (message) addToast(message);
    });

    return () => {
      socket.off("bootstrap");
      socket.off("cells_revealed");
      socket.disconnect();
    };
  }, []);

  // --- draw whenever cells or image change ---
  useEffect(() => {
    drawCanvas();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [revealedCells, imgReady]);

  function drawCanvas() {
    const canvas = canvasRef.current;
    const img = imgRef.current;
    if (!canvas || !img) return;

    const ctx = canvas.getContext("2d");

    // draw grayscale base
    ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    ctx.drawImage(img, 0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

    const imageData = ctx.getImageData(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    const data = imageData.data;
    for (let i = 0; i < data.length; i += 4) {
      const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
      data[i] = avg; // R
      data[i + 1] = avg; // G
      data[i + 2] = avg; // B
    }
    ctx.putImageData(imageData, 0, 0);

    // reveal colored cells
    for (let k = 0; k < revealedCells.length; k++) {
      const { x, y } = revealedCells[k];
      ctx.drawImage(
        img,
        x * CELL_WIDTH,
        y * CELL_HEIGHT,
        CELL_WIDTH,
        CELL_HEIGHT,
        x * CELL_WIDTH,
        y * CELL_HEIGHT,
        CELL_WIDTH,
        CELL_HEIGHT
      );
    }

    // grid overlay
    ctx.strokeStyle = "rgba(255,255,255,0.1)";
    for (let i = 0; i <= GRID_COLUMNS; i++) {
      ctx.beginPath();
      ctx.moveTo(i * CELL_WIDTH, 0);
      ctx.lineTo(i * CELL_WIDTH, CANVAS_HEIGHT);
      ctx.stroke();
    }
    for (let j = 0; j <= GRID_ROWS; j++) {
      ctx.beginPath();
      ctx.moveTo(0, j * CELL_HEIGHT);
      ctx.lineTo(CANVAS_WIDTH, j * CELL_HEIGHT);
      ctx.stroke();
    }
  }

  const remaining = Math.max(totalCells - revealedCells.length, 0);

  return (
    <div style={{ display: "grid", placeItems: "center", padding: 20 }}>
      <div style={{ marginBottom: 12, textAlign: "center" }}>
        <h1>Million Pixel Reveal</h1>
        <p>Cells Remaining: {remaining}</p>
      </div>

      <canvas
        ref={canvasRef}
        width={CANVAS_WIDTH}
        height={CANVAS_HEIGHT}
        style={{
          border: "2px solid #334155",
          borderRadius: 12,
          boxShadow: "0 4px 12px rgba(0,0,0,0.5)",
          maxWidth: "100%",
          height: "auto",
        }}
      />

      {/* Toasts */}
      <div
        style={{
          position: "fixed",
          bottom: 20,
          left: "50%",
          transform: "translateX(-50%)",
          display: "flex",
          flexDirection: "column",
          gap: 8,
          zIndex: 1000,
          pointerEvents: "none",
        }}
      >
        {toasts.map((t) => (
          <div
            key={t.id}
            style={{
              maxWidth: 720,
              background: "rgba(15,23,42,0.92)",
              color: "#e2e8f0",
              border: "1px solid #334155",
              borderRadius: 12,
              padding: "10px 14px",
              boxShadow: "0 6px 24px rgba(0,0,0,0.4)",
              pointerEvents: "auto",
            }}
          >
            <span style={{ opacity: 0.8, marginRight: 8 }}>ðŸ’¬</span>
            <span>{t.text}</span>
          </div>
        ))}
      </div>
    </div>
  );
}
