import React, { useState } from "react";

const API_BASE =
  process.env.REACT_APP_API_BASE ||
  (import.meta?.env && import.meta.env.VITE_API_BASE) ||
  "http://localhost:3001";

// $25/cell preview for custom amounts (UI only; backend enforces with CENTS_PER_CELL=2500)
const CENTS_PER_CELL = 2500;

// Presets now use AMOUNTS (cents), not priceIds
const PRESETS = [
  { label: "$25 (1 cell)", amount: 2500, cells: 1 },
  { label: "$50 (2 cells)", amount: 5000, cells: 2 },
  { label: "$100 (4 cells)", amount: 10000, cells: 4 },
];

export default function PaymentIntro() {
  const [mode, setMode] = useState("preset"); // "preset" | "custom"
  const [selectedPreset, setSelectedPreset] = useState(PRESETS[0]);
  const [customAmount, setCustomAmount] = useState("25.00");
  const [message, setMessage] = useState("");

  const computedCells = Math.floor(
    (Number(customAmount || 0) * 100) / CENTS_PER_CELL
  );

  async function startCheckout(e) {
    e.preventDefault();
    let body;

    if (mode === "preset") {
      if (!selectedPreset) return alert("Choose an option.");
      body = { amount: selectedPreset.amount, currency: "usd", message };
    } else {
      const dollars = Number(customAmount);
      if (!Number.isFinite(dollars) || dollars < 25) {
        return alert("Minimum is $25 (1 cell).");
      }
      body = { amount: Math.round(dollars * 100), currency: "usd", message };
    }

    const res = await fetch(`${API_BASE}/create-checkout-session`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(body),
    });
    const data = await res.json();
    if (!res.ok) return alert(data.error || "Failed to start checkout");
    window.location.href = data.url;
  }

  return (
    <div style={{ maxWidth: 560, margin: "24px auto", padding: 16 }}>
      <h2 style={{ marginBottom: 16 }}>Support the Reveal</h2>

      <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
        <button
          type="button"
          onClick={() => setMode("preset")}
          style={{
            padding: "8px 12px",
            borderRadius: 8,
            border:
              mode === "preset" ? "2px solid #16a34a" : "1px solid #334155",
            background: "transparent",
            color: "inherit",
          }}
        >
          Presets
        </button>
        <button
          type="button"
          onClick={() => setMode("custom")}
          style={{
            padding: "8px 12px",
            borderRadius: 8,
            border:
              mode === "custom" ? "2px solid #16a34a" : "1px solid #334155",
            background: "transparent",
            color: "inherit",
          }}
        >
          Custom
        </button>
      </div>

      <form onSubmit={startCheckout}>
        {mode === "preset" ? (
          <div style={{ display: "flex", gap: 8, marginBottom: 16 }}>
            {PRESETS.map((p) => (
              <label
                key={p.amount}
                style={{
                  border: "1px solid #334155",
                  borderRadius: 8,
                  padding: 12,
                  cursor: "pointer",
                }}
              >
                <input
                  type="radio"
                  name="preset"
                  value={p.amount}
                  checked={selectedPreset?.amount === p.amount}
                  onChange={() => setSelectedPreset(p)}
                  style={{ marginRight: 8 }}
                />
                {p.label}
              </label>
            ))}
          </div>
        ) : (
          <div style={{ marginBottom: 16 }}>
            <label>
              Amount (USD)
              <input
                type="number"
                min="25"
                step="0.01"
                value={customAmount}
                onChange={(e) =>
                  e.target.value === ""
                    ? setCustomAmount("")
                    : setCustomAmount(e.target.value)
                }
                style={{
                  marginLeft: 8,
                  padding: 8,
                  borderRadius: 6,
                  border: "1px solid #334155",
                }}
              />
            </label>
            <div style={{ marginTop: 6, fontSize: 13, opacity: 0.8 }}>
              Will reveal <b>{computedCells}</b> cell
              {computedCells === 1 ? "" : "s"}.
            </div>
          </div>
        )}

        <div style={{ marginBottom: 16 }}>
          <label>
            Message (optional)
            <textarea
              rows={3}
              maxLength={500}
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Say something to be recorded with your purchaseâ€¦"
              style={{
                display: "block",
                width: "100%",
                marginTop: 6,
                padding: 8,
                borderRadius: 8,
                border: "1px solid #334155",
              }}
            />
          </label>
        </div>

        <button
          type="submit"
          style={{
            padding: "10px 16px",
            borderRadius: 10,
            background: "#16a34a",
            color: "white",
            border: "none",
          }}
        >
          Continue to Checkout
        </button>
      </form>
    </div>
  );
}
