import React, { useState } from "react";

const API_BASE =
  process.env.REACT_APP_API_BASE ||
  (import.meta?.env && import.meta.env.VITE_API_BASE) ||
  "http://localhost:3001";

async function postJSON(url, body) {
  const res = await fetch(url, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(body || {}),
  });
  const ct = res.headers.get("content-type") || "";
  if (!ct.includes("application/json")) {
    const text = await res.text();
    throw new Error(
      `Expected JSON but got "${ct}". Body:\n${text.slice(0, 300)}`
    );
  }
  const data = await res.json();
  if (!res.ok) throw new Error(data?.error || "Request failed");
  return data;
}

export default function SimulatePage() {
  const [busy, setBusy] = useState(false);
  const [log, setLog] = useState([]);

  function addLog(line) {
    setLog((l) => [line, ...l].slice(0, 200));
  }

  async function runSingle(amountCents, message) {
    setBusy(true);
    try {
      const data = await postJSON(`${API_BASE}/simulate/purchase`, {
        amountCents,
        message,
      });
      addLog(
        `Single: $${(amountCents / 100).toFixed(2)} → ${data.allocated} cell(s)`
      );
    } catch (e) {
      addLog(`Error: ${e.message}`);
      alert(e.message);
    } finally {
      setBusy(false);
    }
  }

  async function runBatch(entries) {
    setBusy(true);
    try {
      const data = await postJSON(`${API_BASE}/simulate/batch`, { entries });
      addLog(
        `Batch: ${entries.length} events → ${data.totalAllocated} cell(s)`
      );
    } catch (e) {
      addLog(`Error: ${e.message}`);
      alert(e.message);
    } finally {
      setBusy(false);
    }
  }

  // Presets
  const do100x25 = () =>
    runBatch(Array.from({ length: 100 }, () => ({ amountCents: 2500 })));
  const do100x50 = () =>
    runBatch(Array.from({ length: 100 }, () => ({ amountCents: 5000 })));
  const do100x100 = () =>
    runBatch(Array.from({ length: 100 }, () => ({ amountCents: 10000 })));

  const doMixed300 = () => {
    const options = [2500, 5000, 10000];
    const entries = Array.from({ length: 300 }, () => ({
      amountCents: options[Math.floor(Math.random() * options.length)],
    }));
    return runBatch(entries);
  };

  return (
    <div className="simulate-wrap">
      <style>{`
        .simulate-wrap{
          min-height: 100vh;
          padding: 24px 16px 64px;
          display:flex; justify-content:center; align-items:flex-start;
          background: radial-gradient(900px 500px at 0% -10%, #1a2e4a 0%, transparent 40%),
                      radial-gradient(900px 600px at 100% 0%, #1b2d24 0%, transparent 35%),
                      #0b1220;
          color: #e2e8f0;
        }
        .panel{ width: min(960px, 100%); display:grid; grid-template-columns: 1fr 1fr; gap: 16px; }
        .card{
          background: rgba(2,6,23,.7);
          border: 1px solid rgba(148,163,184,.25);
          border-radius: 12px; padding: 16px;
          backdrop-filter: blur(6px);
        }
        .btns{ display:flex; flex-wrap: wrap; gap: 8px; }
        button{
          padding: 10px 14px; border-radius: 10px; border: 1px solid #334155;
          background: #111827; color: #e2e8f0; cursor: pointer;
        }
        button:disabled{ opacity:.5; cursor:not-allowed; }
        .cta{ background: #16a34a; border-color: #16a34a; color: white; }
        .log{ font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
              font-size: 12px; line-height: 1.4; max-height: 360px; overflow:auto; }
        .row{ padding: 6px 0; border-bottom: 1px dashed rgba(148,163,184,.2); }
      `}</style>

      <div className="panel">
        <div className="card">
          <h3>Simulate Purchases</h3>
          <div className="btns" style={{ marginTop: 8 }}>
            <button disabled={busy} onClick={() => runSingle(2500, "Sim $25")}>
              Single $25
            </button>
            <button disabled={busy} onClick={() => runSingle(5000, "Sim $50")}>
              Single $50
            </button>
            <button
              disabled={busy}
              onClick={() => runSingle(10000, "Sim $100")}
            >
              Single $100
            </button>
          </div>

          <h4 style={{ marginTop: 16 }}>Batches</h4>
          <div className="btns">
            <button disabled={busy} onClick={do100x25}>
              100 × $25
            </button>
            <button disabled={busy} onClick={do100x50}>
              100 × $50
            </button>
            <button disabled={busy} onClick={do100x100}>
              100 × $100
            </button>
            <button disabled={busy} className="cta" onClick={doMixed300}>
              300 Mixed
            </button>
          </div>
        </div>

        <div className="card">
          <h3>Log</h3>
          <div className="log" role="log" aria-live="polite">
            {log.map((line, i) => (
              <div className="row" key={i}>
                {line}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
