import React, { useRef, useEffect, useState } from "react";
import imageSrc from "./Banner Hi Rez.png";

const CANVAS_WIDTH = 1495;
const CANVAS_HEIGHT = 1024;
const GRID_COLUMNS = 50;
const GRID_ROWS = 50;
const CELL_WIDTH = CANVAS_WIDTH / GRID_COLUMNS;
const CELL_HEIGHT = CANVAS_HEIGHT / GRID_ROWS;
const MAX_NOTIFICATIONS = 3;

function App() {
  const canvasRef = useRef(null);
  const [revealedCells, setRevealedCells] = useState([]);
  const [speed, setSpeed] = useState(50);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    drawCanvas();
  }, [revealedCells]);

  const drawCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.src = imageSrc;
    img.onload = () => {
      ctx.drawImage(img, 0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

      const imageData = ctx.getImageData(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
      for (let i = 0; i < imageData.data.length; i += 4) {
        const avg =
          (imageData.data[i] + imageData.data[i + 1] + imageData.data[i + 2]) /
          3;
        imageData.data[i] = avg;
        imageData.data[i + 1] = avg;
        imageData.data[i + 2] = avg;
      }
      ctx.putImageData(imageData, 0, 0);

      revealedCells.forEach(({ x, y }) => {
        ctx.drawImage(
          img,
          x * CELL_WIDTH,
          y * CELL_HEIGHT,
          CELL_WIDTH,
          CELL_HEIGHT,
          x * CELL_WIDTH,
          y * CELL_HEIGHT,
          CELL_WIDTH,
          CELL_HEIGHT
        );
      });

      drawGrid(ctx);
    };
  };

  const drawGrid = (ctx) => {
    ctx.strokeStyle = "rgba(255,255,255,0.1)";
    for (let i = 0; i <= GRID_COLUMNS; i++) {
      ctx.beginPath();
      ctx.moveTo(i * CELL_WIDTH, 0);
      ctx.lineTo(i * CELL_WIDTH, CANVAS_HEIGHT);
      ctx.stroke();
    }

    for (let j = 0; j <= GRID_ROWS; j++) {
      ctx.beginPath();
      ctx.moveTo(0, j * CELL_HEIGHT);
      ctx.lineTo(CANVAS_WIDTH, j * CELL_HEIGHT);
      ctx.stroke();
    }
  };

  const simulateFill = () => {
    const unrevealedCells = [];

    for (let x = 0; x < GRID_COLUMNS; x++) {
      for (let y = 0; y < GRID_ROWS; y++) {
        if (!revealedCells.find((cell) => cell.x === x && cell.y === y)) {
          unrevealedCells.push({ x, y });
        }
      }
    }

    const interval = setInterval(() => {
      if (unrevealedCells.length === 0) {
        clearInterval(interval);
        return;
      }

      const index = Math.floor(Math.random() * unrevealedCells.length);
      const cell = unrevealedCells.splice(index, 1)[0];

      setRevealedCells((prev) => [...prev, cell]);
      triggerNotification(`Cell at (${cell.x}, ${cell.y}) revealed!`);
    }, speed);
  };

  const triggerNotification = (message) => {
    const id = Date.now();
    setNotifications((prev) => {
      const updated = [...prev, { id, message }];
      return updated.slice(-MAX_NOTIFICATIONS);
    });
    setTimeout(() => {
      setNotifications((prev) => prev.filter((n) => n.id !== id));
    }, 3000);
  };

  const handleCanvasClick = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const scaleX = canvas.width / rect.width;
    const scaleY = canvas.height / rect.height;

    const canvasX = (e.clientX - rect.left) * scaleX;
    const canvasY = (e.clientY - rect.top) * scaleY;

    const x = Math.floor(canvasX / CELL_WIDTH);
    const y = Math.floor(canvasY / CELL_HEIGHT);

    if (!revealedCells.find((cell) => cell.x === x && cell.y === y)) {
      setRevealedCells([...revealedCells, { x, y }]);
      triggerNotification(`Cell at (${x}, ${y}) revealed!`);
    }
  };

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#1e293b",
        minHeight: "100vh",
        color: "#e2e8f0",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "20px",
        position: "relative",
      }}
    >
      <h1 style={{ fontSize: "2rem", marginBottom: "1rem" }}>
        Million Pixel Reveal
      </h1>
      <div
        style={{
          marginBottom: "20px",
          display: "flex",
          alignItems: "center",
          gap: "12px",
        }}
      >
        <button
          onClick={simulateFill}
          style={{
            padding: "10px 20px",
            borderRadius: "8px",
            border: "none",
            backgroundColor: "#3b82f6",
            color: "white",
            cursor: "pointer",
          }}
        >
          Simulate Fill
        </button>
        <label>
          Speed: {speed}ms
          <input
            type="range"
            min="10"
            max="200"
            step="10"
            value={speed}
            onChange={(e) => setSpeed(Number(e.target.value))}
            style={{ marginLeft: "10px" }}
          />
        </label>
      </div>
      <div
        style={{
          backgroundColor: "#1e293b",
          padding: "12px",
          borderRadius: "16px",
          boxShadow: "0 6px 20px rgba(0,0,0,0.5)",
          overflow: "hidden",
        }}
      >
        <canvas
          ref={canvasRef}
          width={CANVAS_WIDTH}
          height={CANVAS_HEIGHT}
          onClick={handleCanvasClick}
          style={{
            border: "none",
            cursor: "pointer",
            maxWidth: "100%",
            height: "auto",
            backgroundColor: "#0f172a",
            display: "block",
          }}
        />
      </div>
      <div
        style={{
          position: "fixed",
          top: "20px",
          right: "20px",
          display: "flex",
          flexDirection: "column",
          gap: "8px",
        }}
      >
        {notifications.map((n) => (
          <div
            key={n.id}
            style={{
              backgroundColor: "#334155",
              padding: "10px 20px",
              borderRadius: "8px",
              boxShadow: "0 2px 6px rgba(0,0,0,0.3)",
              color: "#fff",
            }}
          >
            {n.message}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
