import React, { useEffect, useMemo, useRef, useState } from "react";
import { io } from "socket.io-client";
import imageSrc from "../Banner Hi Rez.png";

const API_BASE =
  process.env.REACT_APP_API_BASE ||
  (import.meta?.env && import.meta.env.VITE_API_BASE) ||
  "http://localhost:3001";
const socket = io(API_BASE, { transports: ["websocket"] });

const CANVAS_WIDTH = 1495;
const CANVAS_HEIGHT = 1024;
const GRID_COLUMNS = 200;
const GRID_ROWS = 200;
const CELL_WIDTH = CANVAS_WIDTH / GRID_COLUMNS;
const CELL_HEIGHT = CANVAS_HEIGHT / GRID_ROWS;

const pulseDurationMs = 2500;

// cache for grayscale base
let cachedGrayCanvas = null;
function buildGrayBase(img) {
  if (cachedGrayCanvas) return cachedGrayCanvas;
  const off = document.createElement("canvas");
  off.width = CANVAS_WIDTH;
  off.height = CANVAS_HEIGHT;
  const octx = off.getContext("2d");
  octx.drawImage(img, 0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
  const data = octx.getImageData(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
  const a = data.data;
  for (let i = 0; i < a.length; i += 4) {
    const avg = (a[i] + a[i + 1] + a[i + 2]) / 3;
    a[i] = avg;
    a[i + 1] = avg;
    a[i + 2] = avg;
  }
  octx.putImageData(data, 0, 0);
  cachedGrayCanvas = off;
  return off;
}

const k = (x, y) => `${x},${y}`;

export default function CanvasPage() {
  const canvasRef = useRef(null);

  // revealed grid + O(1) lookup
  const [cells, setCells] = useState([]); // [{x,y,amountCents?,message?,ts?}]
  const cellsMapRef = useRef(new Map());

  // image
  const imgRef = useRef(null);
  const [imgReady, setImgReady] = useState(false);

  // hover tooltip
  const [hover, setHover] = useState(null); // { clientX, clientY, cell }

  // pulse/highlight animation
  const pulsesRef = useRef(new Map()); // key -> t0 (performance.now)
  const rafRef = useRef(0);

  // message bubble
  const [bubble, setBubble] = useState(null); // { left, top, message, amountCents, until }
  const bubbleTimerRef = useRef(null);

  const totalCells = GRID_COLUMNS * GRID_ROWS;

  // load image
  useEffect(() => {
    const img = new Image();
    img.src = imageSrc;
    img.onload = () => {
      imgRef.current = img;
      buildGrayBase(img);
      setImgReady(true);
      draw();
    };
  }, []);

  // bootstrap + live sockets
  useEffect(() => {
    const merge = (incoming) => {
      if (!incoming?.length) return;
      setCells((prev) => {
        const out = [...prev];
        const seen = new Set(prev.map((c) => k(c.x, c.y)));
        for (const c of incoming) {
          const key = k(c.x, c.y);
          if (!seen.has(key)) {
            out.push(c);
            seen.add(key);
            cellsMapRef.current.set(key, c);
          } else {
            const idx = out.findIndex((p) => p.x === c.x && p.y === c.y);
            if (idx >= 0) out[idx] = { ...out[idx], ...c };
            cellsMapRef.current.set(key, {
              ...cellsMapRef.current.get(key),
              ...c,
            });
          }
        }
        return out;
      });
    };

    fetch(`${API_BASE}/state`)
      .then((r) => r.json())
      .then(({ cells }) => merge(cells))
      .catch(() => {});

    socket.on("bootstrap", ({ cells }) => merge(cells));

    socket.on("cells_revealed", ({ cells, message, amountCents }) => {
      merge(cells);
      // mark pulses
      const t0 = performance.now();
      for (const c of cells) pulsesRef.current.set(k(c.x, c.y), t0);
      kickPulse();
      // bubble near the first cell (if message present)
      if (message) showBubbleForCells(cells, message, amountCents);
    });

    socket.on("donation_message", ({ message, amountCents }) => {
      if (message) showBubbleForCells([], message, amountCents);
    });

    return () => {
      socket.off("bootstrap");
      socket.off("cells_revealed");
      socket.off("donation_message");
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    draw(); /* redraw on data changes */
  }, [cells, imgReady]);

  function draw(now = performance.now()) {
    const canvas = canvasRef.current;
    const ctx = canvas?.getContext("2d");
    const img = imgRef.current;
    if (!canvas || !ctx || !img) return;

    const gray = buildGrayBase(img);
    ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    ctx.drawImage(gray, 0, 0);

    // draw revealed tiles (actual image)
    for (let i = 0; i < cells.length; i++) {
      const { x, y } = cells[i];
      ctx.drawImage(
        img,
        x * CELL_WIDTH,
        y * CELL_HEIGHT,
        CELL_WIDTH,
        CELL_HEIGHT,
        x * CELL_WIDTH,
        y * CELL_HEIGHT,
        CELL_WIDTH,
        CELL_HEIGHT
      );
    }

    // pulse overlay (fade)
    let anyAlive = false;
    pulsesRef.current.forEach((t0, keyStr) => {
      const age = now - t0;
      const life = age / pulseDurationMs;
      if (life >= 1) {
        pulsesRef.current.delete(keyStr);
        return;
      }
      anyAlive = true;
      const [xs, ys] = keyStr.split(",");
      const x = +xs,
        y = +ys;
      const alpha = 1 - life; // 1 -> 0
      ctx.save();
      ctx.globalAlpha = Math.max(0, alpha) * 0.95;
      ctx.lineWidth = 2;
      ctx.strokeStyle = "rgba(34,197,94,1)";
      ctx.strokeRect(
        Math.floor(x * CELL_WIDTH) + 0.5,
        Math.floor(y * CELL_HEIGHT) + 0.5,
        Math.floor(CELL_WIDTH) - 1,
        Math.floor(CELL_HEIGHT) - 1
      );
      ctx.restore();
    });

    // grid on top
    drawGrid(ctx);

    if (anyAlive) {
      rafRef.current = requestAnimationFrame(draw);
    } else if (rafRef.current) {
      cancelAnimationFrame(rafRef.current);
      rafRef.current = 0;
    }
  }

  function kickPulse() {
    if (!rafRef.current) rafRef.current = requestAnimationFrame(draw);
  }

  function drawGrid(ctx) {
    ctx.save();
    ctx.strokeStyle = "rgba(255,255,255,0.06)";
    ctx.lineWidth = 1;
    for (let i = 0; i <= GRID_COLUMNS; i++) {
      ctx.beginPath();
      ctx.moveTo(i * CELL_WIDTH, 0);
      ctx.lineTo(i * CELL_WIDTH, CANVAS_HEIGHT);
      ctx.stroke();
    }
    for (let j = 0; j <= GRID_ROWS; j++) {
      ctx.beginPath();
      ctx.moveTo(0, j * CELL_HEIGHT);
      ctx.lineTo(CANVAS_WIDTH, j * CELL_HEIGHT);
      ctx.stroke();
    }
    ctx.restore();
  }

  // ----- hover -----
  function onMouseMove(e) {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const scaleX = CANVAS_WIDTH / rect.width;
    const scaleY = CANVAS_HEIGHT / rect.height;
    const px = (e.clientX - rect.left) * scaleX;
    const py = (e.clientY - rect.top) * scaleY;
    const x = Math.floor(px / CELL_WIDTH);
    const y = Math.floor(py / CELL_HEIGHT);
    if (x < 0 || y < 0 || x >= GRID_COLUMNS || y >= GRID_ROWS) {
      setHover(null);
      return;
    }
    const cell = cellsMapRef.current.get(k(x, y));
    if (!cell) {
      setHover(null);
      return;
    }
    setHover({ clientX: e.clientX, clientY: e.clientY, cell });
  }
  function onMouseLeave() {
    setHover(null);
  }

  // ----- bubble -----
  function showBubbleForCells(cellsArr, message, amountCents) {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();

    let left = rect.left + rect.width / 2;
    let top = rect.top + 24;

    const first = cellsArr?.[0];
    if (first) {
      left = rect.left + (first.x + 0.5) * (rect.width / GRID_COLUMNS);
      top = rect.top + (first.y + 0.5) * (rect.height / GRID_ROWS) - 8;
    }

    setBubble({
      left,
      top,
      message,
      amountCents,
      until: Date.now() + 5000,
    });

    if (bubbleTimerRef.current) clearTimeout(bubbleTimerRef.current);
    bubbleTimerRef.current = setTimeout(() => setBubble(null), 5000);
  }

  const remaining = useMemo(
    () => Math.max(totalCells - cells.length, 0),
    [cells.length, totalCells]
  );

  return (
    <div className="canvas-wrap">
      <Style />
      <div className="canvas-shell">
        {/* HUD (DOM) */}
        <div className="hud">
          <div className="hud-title">Million Pixel Reveal</div>
          <div className="hud-sub">Remaining: {remaining.toLocaleString()}</div>
        </div>

        <canvas
          ref={canvasRef}
          width={CANVAS_WIDTH}
          height={CANVAS_HEIGHT}
          className="canvas"
          onMouseMove={onMouseMove}
          onMouseLeave={onMouseLeave}
        />

        {/* Hover tooltip */}
        {hover?.cell && (
          <div
            className="tip"
            style={{ left: hover.clientX + 14, top: hover.clientY + 14 }}
          >
            <div className="tip-row">
              <b>Cell</b> ({hover.cell.x},{hover.cell.y})
            </div>
            {typeof hover.cell.amountCents === "number" && (
              <div className="tip-row">
                <b>Amount</b> ${(hover.cell.amountCents / 100).toFixed(2)}
              </div>
            )}
            {hover.cell.message && (
              <div className="tip-row msg">“{hover.cell.message}”</div>
            )}
          </div>
        )}

        {/* Message bubble near new cell or top center */}
        {bubble && (
          <div
            className="bubble"
            style={{ left: bubble.left, top: bubble.top }}
          >
            {typeof bubble.amountCents === "number" && (
              <div className="bubble-amt">
                ${(bubble.amountCents / 100).toFixed(2)}
              </div>
            )}
            {bubble.message && (
              <div className="bubble-msg">“{bubble.message}”</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

