import React, { useRef, useEffect, useState } from "react";
import imageSrc from "./Banner Hi Rez.png";

const CANVAS_WIDTH = 1495;
const CANVAS_HEIGHT = 1024;
const GRID_COLUMNS = 50;
const GRID_ROWS = 50;
const CELL_WIDTH = CANVAS_WIDTH / GRID_COLUMNS;
const CELL_HEIGHT = CANVAS_HEIGHT / GRID_ROWS;

function App() {
  const canvasRef = useRef(null);
  const [revealedCells, setRevealedCells] = useState([]);
  const [speed, setSpeed] = useState(100);
  const [autoFilling, setAutoFilling] = useState(false);
  const intervalRef = useRef(null);
  const totalCells = GRID_COLUMNS * GRID_ROWS;

  useEffect(() => {
    drawCanvas();
  }, [revealedCells]);

  const drawCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.src = imageSrc;
    img.onload = () => {
      ctx.drawImage(img, 0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

      const imageData = ctx.getImageData(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
      for (let i = 0; i < imageData.data.length; i += 4) {
        const avg =
          (imageData.data[i] + imageData.data[i + 1] + imageData.data[i + 2]) /
          3;
        imageData.data[i] = avg;
        imageData.data[i + 1] = avg;
        imageData.data[i + 2] = avg;
      }
      ctx.putImageData(imageData, 0, 0);

      revealedCells.forEach(({ x, y }) => {
        ctx.drawImage(
          img,
          x * CELL_WIDTH,
          y * CELL_HEIGHT,
          CELL_WIDTH,
          CELL_HEIGHT,
          x * CELL_WIDTH,
          y * CELL_HEIGHT,
          CELL_WIDTH,
          CELL_HEIGHT
        );
      });

      drawGrid(ctx);
    };
  };

  const drawGrid = (ctx) => {
    ctx.strokeStyle = "rgba(255,255,255,0.1)";
    for (let i = 0; i <= GRID_COLUMNS; i++) {
      ctx.beginPath();
      ctx.moveTo(i * CELL_WIDTH, 0);
      ctx.lineTo(i * CELL_WIDTH, CANVAS_HEIGHT);
      ctx.stroke();
    }

    for (let j = 0; j <= GRID_ROWS; j++) {
      ctx.beginPath();
      ctx.moveTo(0, j * CELL_HEIGHT);
      ctx.lineTo(CANVAS_WIDTH, j * CELL_HEIGHT);
      ctx.stroke();
    }
  };

  const getAvailableCells = () => {
    const available = [];
    for (let x = 0; x < GRID_COLUMNS; x++) {
      for (let y = 0; y < GRID_ROWS; y++) {
        if (!revealedCells.find((cell) => cell.x === x && cell.y === y)) {
          available.push({ x, y });
        }
      }
    }
    return available;
  };

  const revealBulkCells = (quantity) => {
    const available = getAvailableCells();
    if (available.length === 0) return;

    const start = available[Math.floor(Math.random() * available.length)];
    const group = [start];

    while (group.length < quantity && group.length < available.length) {
      const neighbors = getNeighbors(group, available);
      if (neighbors.length === 0) {
        const remaining = available.filter(
          (a) => !group.find((g) => g.x === a.x && g.y === a.y)
        );
        if (remaining.length === 0) break;
        group.push(remaining[Math.floor(Math.random() * remaining.length)]);
      } else {
        const next = neighbors[Math.floor(Math.random() * neighbors.length)];
        group.push(next);
      }
    }

    setRevealedCells((prev) => [...prev, ...group]);
  };

  const getNeighbors = (currentGroup, available) => {
    const neighbors = [];
    currentGroup.forEach(({ x, y }) => {
      const possible = [
        { x: x + 1, y },
        { x: x - 1, y },
        { x, y: y + 1 },
        { x, y: y - 1 },
      ];
      possible.forEach((cell) => {
        if (
          cell.x >= 0 &&
          cell.y >= 0 &&
          cell.x < GRID_COLUMNS &&
          cell.y < GRID_ROWS &&
          available.find((a) => a.x === cell.x && a.y === cell.y) &&
          !currentGroup.find((g) => g.x === cell.x && g.y === cell.y)
        ) {
          neighbors.push(cell);
        }
      });
    });
    return neighbors;
  };

  const startAutoFill = () => {
    if (intervalRef.current) return;
    setAutoFilling(true);
    intervalRef.current = setInterval(() => {
      if (getAvailableCells().length === 0) {
        stopAutoFill();
        return;
      }
      revealBulkCells(1);
    }, speed);
  };

  const stopAutoFill = () => {
    clearInterval(intervalRef.current);
    intervalRef.current = null;
    setAutoFilling(false);
  };

  const remainingCells = Math.max(totalCells - revealedCells.length, 0);

  return (
    <div
      style={{
        fontFamily: "Arial, sans-serif",
        backgroundColor: "#1e293b",
        minHeight: "100vh",
        color: "#e2e8f0",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "20px",
      }}
    >
      <h1>Million Pixel Reveal</h1>
      <p>Cells Remaining: {remainingCells}</p>
      <div style={{ marginBottom: "20px" }}>
        <button
          onClick={() => revealBulkCells(1)}
          style={{ marginRight: "10px", padding: "10px 20px" }}
        >
          Reveal 1 Cell
        </button>
        <button
          onClick={() => revealBulkCells(5)}
          style={{ marginRight: "10px", padding: "10px 20px" }}
        >
          Reveal 5 Cells
        </button>
        <button
          onClick={() => revealBulkCells(10)}
          style={{ marginRight: "10px", padding: "10px 20px" }}
        >
          Reveal 10 Cells
        </button>
        <button
          onClick={startAutoFill}
          style={{
            marginRight: "10px",
            padding: "10px 20px",
            backgroundColor: "#16a34a",
            color: "white",
          }}
        >
          Start Auto Fill
        </button>
        <button
          onClick={stopAutoFill}
          style={{
            padding: "10px 20px",
            backgroundColor: "#dc2626",
            color: "white",
          }}
        >
          Stop Auto Fill
        </button>
      </div>
      <div style={{ marginBottom: "20px" }}>
        <label>Speed: {speed}ms</label>
        <input
          type="range"
          min="10"
          max="1000"
          step="10"
          value={speed}
          onChange={(e) => setSpeed(Number(e.target.value))}
          style={{ marginLeft: "10px" }}
        />
      </div>
      <canvas
        ref={canvasRef}
        width={CANVAS_WIDTH}
        height={CANVAS_HEIGHT}
        style={{
          border: "2px solid #334155",
          borderRadius: "12px",
          boxShadow: "0 4px 12px rgba(0,0,0,0.5)",
          cursor: "pointer",
          maxWidth: "100%",
          height: "auto",
        }}
      />
    </div>
  );
}

export default App;
